import React from 'react';
import { useDispatch } from 'react-redux';
import { onClose } from '../../actions';
import '../../styles/Card.scss';
import {Link} from 'react-router-dom'

import {CloseCircleOutlined} from '@ant-design/icons'

export default function Card ({min, max, name, img, id}) {
  console.log(id)
  const dispatch = useDispatch()

    return (
      <div className="card">
          <div id="closeIcon" className="row">
              <button onClick={()=>dispatch(onClose(id))}  style={{backgroundColor:'transparent',border:'none'}} ><CloseCircleOutlined style={{color:'red'}} /></button>
          </div>
          <div className="card-body">
            <Link to={`/ciudad/${id}`} style={{textDecoration: 'none'}}>
            <h5 className="card-title" id={id}>{name}</h5>
            </Link>
            <div className="imgN">
                <img className="iconoClima" src={"https://openweathermap.org/img/wn/"+img+"@2x.png"}  alt="icnono clima" />
            </div>
            <div className="row">
              <div  style={{color:'white'}} className="col">
                  <p>Min</p>
                  <p>{min}°</p>
              </div>
              <div style={{color:'white'}} className="col">
                <p>Max</p>
                <p>{max}°</p>
              </div>
            </div>
          </div>
      </div>
    );
};
