import {
    GET_CUIDAD,
    GET_DETAILS,
    REMOVE_CUIDAD
} from '../actions/index.js';

const initialState = {
    cuidades:[],
    details:{}

}

export default function rootReducers(state = initialState, action) {

    const {type, payload}=action

    switch (type) {
        case GET_CUIDAD:
            var search = [...state.ciudades].find(e=>e.id===payload.id).concat()
            if(search.length>0){
                var resto= [...state.ciudades].filter(c => c.id !==payload.id)
                var res= search.concat(resto)
            }

            return{
                ...state,
                cuidades:res.length>0? res : payload.img?  [payload,...state,cuidades] : [...state.cuidades]

            }
        case GET_DETAILS:
            return{
                ...state,
                details: payload
            }
        case REMOVE_CUIDAD:
            return{
                ...state,
                cuidades:[...state.cuidades].filter(c => c.id !== Number(id))
            }
        default: return state
    }
}
