const apiKey = '4ae2636d8dfbdc3044bede63951a019b';

export const GET_CUIDAD ='GET_CIUDAD';
export const GET_DETAILS = 'GET_DETAILS'
export const REMOVE_CUIDAD = 'REMOVE_CUIDAD'

export function onSearch(cuidad){
    return async function(dispatch){
        var recurso= await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}`)
            recurso= await recurso.json()
            const ciudad = {
                min: Math.round(recurso.main.temp_min),
                max: Math.round(recurso.main.temp_max),
                img: recurso.weather[0].icon,
                id: recurso.id,
                name: recurso.name
            }
        return dispatch({type:GET_CUIDAD, payload:cuidad})
    }
};

export function onSearchInicio(lat,lon){
    return async function(dispatch){
        var recurso= await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${apiKey}`)
            recurso= await recurso.json()
            const ciudad = {
                min: Math.round(recurso.main.temp_min),
                max: Math.round(recurso.main.temp_max),
                img: recurso.weather[0].icon,
                id: recurso.id,
                name: recurso.name
            }
        return dispatch({type:GET_CUIDAD, payload:cuidad})
    }
};

export function details(cuidad){
    return async function(dispatch){
        var recurso= await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}`)
            recurso= await recurso.json()
            const ciudad = {
                min: Math.round(recurso.main.temp_min),
                max: Math.round(recurso.main.temp_max),
                img: recurso.weather[0].icon,
                id: recurso.id,
                wind: recurso.wind.speed,
                temp: recurso.main.temp,
                name: recurso.name,
                weather: recurso.weather[0].main,
                clouds: recurso.clouds.all,
                latitud: recurso.coord.lat,
                longitud: recurso.coord.lon
            }
        return dispatch({type:GET_CDETAILS, payload:cuidad})
    }
};

export function onClose(id){
    return{
     type: REMOVE_CUIDAD,
     payload:id
    }
}
