const apiKey = '4ae2636d8dfbdc3044bede63951a019b';

export async function buscar({name}){
    var recurso= await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${name}&appid=${apiKey}`)
    recurso= await recurso.json()

    return await recurso
}
