import React, { useState } from "react";

import './Search.css'

export default function SearchBar({onSearch}) {
  const [city, setCity] = useState("");
  return (
    <div className='form' >
      <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
      setCity('')
    }}>
      <input type="submit" value="Agregar" />
      <input
        className='input'
        type="text"
        placeholder="Ciudad..."
        value={city}
        onChange={e => setCity(e.target.value)}
      />
    </form>
    </div>

  );
}
